import './WordCounter.css'

function WordCounter() {
    return (
        <div id='container'>
            <p>Font size picker</p>
            <label htmlFor="char-limit-input">Word limit input</label>
            <input type="number" id="char-limit-input" />
            <br /> <br /><textarea name=""></textarea>
            <div id="word-counter">Total number of words written{ }</div>
            <br />
            <br />
            <div id="char-counter" >Total number of characters used{ }</div>
        </div>
    );
}

export default WordCounter;
